  getAllBlogs: function getAllBlogs() {
    return {
      type: blogActions.GET_ALL_BLOGS_REQUEST
    };
  },
  getBlog: function getBlog(blogId) {
    return {
      type: blogActions.GET_BLOG_REQUEST,
      blogId: blogId
    };
  },
  addBlog: function addBlog(newBlog) {
    return {
      type: blogActions.ADD_BLOG_REQUEST,
      blog: newBlog
    };
  },
  updateBlog: function updateBlog(newBlog) {
    return {
      type: blogActions.UPDATE_BLOG_REQUEST,
      blog: newBlog
    };
  },
  deleteBlog: function deleteBlog(blogId) {
    return {
      type: blogActions.DELETE_BLOG_REQUEST,
      blogId: blogId
    };
  }
};
