import React, {useEffect, useState} from "react";
import clone from "clone";
import Link from "next/link";
import Box from "@iso/components/utility/box";
import HelperText from "@iso/components/utility/helper-text";
import LayoutWrapper from "@iso/components/utility/layoutWrapper";
import PageHeader from "@iso/components/utility/pageHeader";
import Scrollbars from "@iso/components/utility/customScrollBar";
import Button from "@iso/components/uielements/button";
import Popconfirm from "@iso/components/Feedback/Popconfirm";
import {
    DateCell,
    ImageCell,
    LinkCell,
    TextCell,
} from "@iso/components/Tables/HelperCells";
import {EditOutlined, DeleteOutlined} from "@ant-design/icons";
import TableWrapper from "@iso/containers/Tables/AntTables/AntTables.styles";

import categoryActions from "@redux/categories/actions";
import {useDispatch, useSelector} from "react-redux";
import {Divider, Radio} from "antd";
import propertyActions from "@redux/properties/actions";


const tableColumns = [
    {
        title: "ID",
        key: "id",
        width: 100,
        render: (object) => <p>{object.id}</p>,
    },
    {
        title: "Name",
        key: "name",
        width: 100,
        render: (object) => <p>{object.name}</p>,
    },
    {
        title: "Address",
        key: "address",
        width: 200,
        render: (object) => <p>{object.address}</p>,
    },
];

export default function SimilarProperty(props) {
    const {selectedItems} = props
    const dispatch = useDispatch();

    const {items, loading} = useSelector((state) => state.Properties);
    const [state, setState] = useState({
        columns: tableColumns,
        dataList: items,
    });

    useEffect(() => {
        dispatch(propertyActions.getAllProperties());
    }, []);

    useEffect(() => {
        setState({
            ...state,
            dataList: items.map(item => ({
                key: item.id,
                ...item,
            })),
        });
    }, [items]);

    const {columns, dataList} = state;

    const rowSelection = {
        onChange: (selectedRowKeys, selectedRows) => {
            console.log(`selectedRowKeys: ${selectedRowKeys}`, 'selectedRows: ', selectedRows);
        },
        getCheckboxProps: (record) => ({
            disabled: record.name === 'Disabled User',
            // Column configuration not to be checked
            name: record.name,
        }),
    };

    return (
        <LayoutWrapper>
            <PageHeader>Category</PageHeader>
            <Box>

                {items?.length === 0 ? (
                    <HelperText text="No Properties found"/>
                ) : (
                    <div className="isoPropertyTable">
                        <Scrollbars
                            style={{width: "100%", height: "calc(60vh - 70px)"}}
                        >
                            <TableWrapper
                                rowSelection={{
                                    type: 'checkbox',
                                    ...rowSelection,
                                }}
                                columns={columns}
                                dataSource={items}
                                className="categoryListTable"
                            />
                        </Scrollbars>
                    </div>
                )}
            </Box>
        </LayoutWrapper>
    );
}
